const response1 = await insomnia.send();
expect(response1.status).to.equal(200);
let data = JSON.parse(response1.data);

expect(data.current_block_identifier.index).to.be.an("number");
expect(data.current_block_identifier.hash).to.be.an("string");
expect(data.current_block_timestamp).to.be.an("number");
expect(data.genesis_block_identifier.index).to.equal(0);
expect(data.genesis_block_identifier.hash).to.equal("000000000019d6689c085ae165831e934ff763ae46a2a6c172b3f1b60a8ce26f");
for (var peer of data.peers) {
  expect(peer.peer_id).to.be.an("string");
  expect(peer.metadata.addr).to.be.an("string");
  expect(peer.metadata.banscore).to.be.an("number");
  expect(peer.metadata.lastrecv).to.be.an("number");
  expect(peer.metadata.lastsend).to.be.an("number");
